mapper.select:
select a.STAFF_ID,d.STAFF_NAME, a.PERMISSION_Id,PERMISSION_NAME,a.DEPARTMENT_ID,DEPARTMENT_NAME from t_black_list a,t_department b,t_permission c,t_staff d
a.DEPARTMENT_ID = b.DEPARTMENT_ID and a.PERMISSION_ID = c.PERMISSION_ID and a.STAFF_ID = d.STAFF_ID ${ew.sqlSegment}
order by a.STAFF_ID ASC

mapper.delete:
delete from t_black_list
where

    (STAFF_ID = #{item.staffId}
    and DEPARTMENT_ID = #{item.departmentId}
    and PERMISSION_ID = #{item.permissionId})
